import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ContainerTable = () => {
  const [containers, setContainers] = useState([]);
  const [editingContainer, setEditingContainer] = useState(null);
  const [newContainer, setNewContainer] = useState({ address: '' });
  const [showDetails, setShowDetails] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: 'id', direction: 'asc' });
  const [validationError, setValidationError] = useState('');

  // Валидация адреса
  const validateAddress = (address) => {
    if (address.length > 25) {
      setValidationError('IP address must be 25 characters or less');
      return false;
    }
    setValidationError('');
    return true;
  };

  // Обработчики изменений
  const handleNewAddressChange = (e) => {
    const value = e.target.value;
    if (validateAddress(value)) {
      setNewContainer({ address: value });
    }
  };

  const handleEditAddressChange = (e) => {
    const value = e.target.value;
    if (validateAddress(value)) {
      setEditingContainer(prev => ({
        ...prev,
        address: value
      }));
    }
  };

  // Модифицированные CRUD-методы
  const handleCreate = async () => {
    if (!validateAddress(newContainer.address)) return;

    try {
      await axios.post('http://localhost:8080/container', newContainer);
      setNewContainer({ address: '' });
      fetchContainers();
    } catch (error) {
      console.error('Error creating container:', error);
    }
  };

  const handleUpdate = async () => {
    if (!validateAddress(editingContainer.address)) return;

    try {
      await axios.put('http://localhost:8080/container', editingContainer);
      setEditingContainer(null);
      fetchContainers();
    } catch (error) {
      console.error('Error updating container:', error);
    }
  };

  // Остальной код остается без изменений до render()

  return (
    <div className="container">
      <h2>Container Listing</h2>

      {/* Обновленная форма добавления */}
      <div className="add-form">
        <input
          type="text"
          placeholder="Enter IP Address"
          value={newContainer.address}
          onChange={handleNewAddressChange}
          maxLength={25}
        />
        <button 
          onClick={handleCreate} 
          disabled={!newContainer.address || !!validationError}
        >
          Add New
        </button>
        {validationError && (
          <div style={{ color: 'red', marginTop: '5px' }}>{validationError}</div>
        )}
      </div>
      {/* Table */}
      <table>
        <thead>
          <tr>
            <th 
              onClick={() => handleSort('id')}
              style={{ cursor: 'pointer' }}
            >
              ID {sortConfig.key === 'id' && (
                sortConfig.direction === 'asc' ? '↑' : '↓'
              )}
            </th>
            <th>IP Address</th>
            <th>Last Ping</th>
            <th>Last Success Ping</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {sortedContainers.map((container) => (
            <tr key={container.id}>
              <td>{container.id}</td>
              <td>
                {editingContainer?.id === container.id ? (
                  <input
                    value={editingContainer.address}
                    onChange={handleEditAddressChange}
                    maxLength={25}
                  />
                ) : (
                  container.address
                )}
              </td>
              <td>
                {editingContainer?.id === container.id ? (
                  <input
                    value={editingContainer.address}
                    onChange={(e) => setEditingContainer({
                      ...editingContainer,
                      address: e.target.value
                    })}
                  />
                ) : (
                  container.address
                )}
              </td>
              <td>{formatDate(container.last_ping)}</td>
              <td>{formatDate(container.last_success_ping)}</td>
              <td>
                {editingContainer?.id === container.id ? (
                  <>
                    <button onClick={handleUpdate}>Save</button>
                    <button onClick={() => setEditingContainer(null)}>Cancel</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => setEditingContainer(container)}>Edit</button>
                    <button onClick={() => handleDelete(container.id)}>Remove</button>
                    <button onClick={() => setShowDetails(container)}>Details</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Details Modal */}
      {showDetails && (
        <div className="modal">
          <h3>Container Details</h3>
          <p>ID: {showDetails.id}</p>
          <p>IP Address: {showDetails.address}</p>
          <p>Last Ping: {formatDate(showDetails.last_ping)}</p>
          <p>Last Success Ping: {formatDate(showDetails.last_success_ping)}</p>
          <button onClick={() => setShowDetails(null)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default ContainerTable;
