import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = "http://localhost:8080"; // Добавляем базовый URL

const ContainerTable = () => {
  const [containers, setContainers] = useState([]);
  const [editingContainer, setEditingContainer] = useState(null);
  const [newContainer, setNewContainer] = useState({ address: '' });
  const [showDetails, setShowDetails] = useState(null);
  const [error, setError] = useState(''); // Состояние для ошибок

  useEffect(() => {
    fetchContainers();
  }, []);

  const fetchContainers = async () => {
    try {
      // Исправляем URL, добавляя протокол http://
      const response = await axios.get(`${API_URL}/containers`);
      setContainers(response.data);
      setError('');
    } catch (error) {
      handleError(error, 'Error fetching containers:');
    }
  };

  const handleCreate = async () => {
    try {
      await axios.post(`${API_URL}/containers`, newContainer); // Проверьте путь /containers vs /container
      setNewContainer({ address: '' });
      fetchContainers();
    } catch (error) {
      handleError(error, 'Error creating container:');
    }
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`${API_URL}/containers/${editingContainer.id}`, editingContainer); // Добавляем ID в URL
      setEditingContainer(null);
      fetchContainers();
    } catch (error) {
      handleError(error, 'Error updating container:');
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/containers/${id}`); // Проверьте путь /containers
      fetchContainers();
    } catch (error) {
      handleError(error, 'Error deleting container:');
    }
  };

  // Улучшенная обработка ошибок
  const handleError = (error, context) => {
    let errorMessage = context;
    if (error.response) {
      errorMessage += ` Server responded with: ${error.response.status}`;
    } else if (error.request) {
      errorMessage += ' No response from server';
    } else {
      errorMessage += ` ${error.message}`;
    }
    setError(errorMessage);
    console.error(errorMessage, error);
  };


  return (
    <div className="container">
      <h2>Container Listing</h2>

      {/* Add New Form */}
      <div className="add-form">
        <input
          type="text"
          placeholder="Enter IP Address"
          value={newContainer.address}
          onChange={(e) => setNewContainer({ address: e.target.value })}
        />
        <button onClick={handleCreate}>Add New</button>
      </div>

      {/* Table */}
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>IP Address</th>
            <th>Last Ping</th>
            <th>Last Success Ping</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {containers.map((container) => (
            <tr key={container.id}>
              <td>{container.id}</td>
              <td>
                {editingContainer?.id === container.id ? (
                  <input
                    value={editingContainer.address}
                    onChange={(e) => setEditingContainer({
                      ...editingContainer,
                      address: e.target.value
                    })}
                  />
                ) : (
                  container.address
                )}
              </td>
              <td>{formatDate(container.last_ping)}</td>
              <td>{formatDate(container.last_success_ping)}</td>
              <td>
                {editingContainer?.id === container.id ? (
                  <>
                    <button onClick={handleUpdate}>Save</button>
                    <button onClick={() => setEditingContainer(null)}>Cancel</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => setEditingContainer(container)}>Edit</button>
                    <button onClick={() => handleDelete(container.id)}>Remove</button>
                    <button onClick={() => setShowDetails(container)}>Details</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Details Modal */}
      {showDetails && (
        <div className="modal">
          <h3>Container Details</h3>
          <p>ID: {showDetails.id}</p>
          <p>IP Address: {showDetails.address}</p>
          <p>Last Ping: {formatDate(showDetails.last_ping)}</p>
          <p>Last Success Ping: {formatDate(showDetails.last_success_ping)}</p>
          <button onClick={() => setShowDetails(null)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default ContainerTable;
